    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
      aktif(true);
       int baris = jTable1.getSelectedRow();
       txtid.setText(jTable1.getModel().getValueAt(baris, 0).toString());
       txtnama.setText(jTable1.getModel().getValueAt(baris, 1).toString());
       dokter_bagian.setSelectedItem(jTable1.getModel().getValueAt(baris, 2).toString());
       jkelamin.setSelectedItem(jTable1.getModel().getValueAt(baris, 3).toString());
       alamat.setText(jTable1.getModel().getValueAt(baris, 4).toString());
       kota.setText(jTable1.getModel().getValueAt(baris, 5).toString());
       telepon.setText(jTable1.getModel().getValueAt(baris, 6).toString());
       bthapus.setEnabled(true);
       btedit.setEnabled(true);
       btbatal.setEnabled(true);
    }//GEN-LAST:event_jTable1MouseClicked

