    private void tampilkan_data() {
        DefaultTableModel tabelmapel = new DefaultTableModel();
        tabelmapel.addColumn("KODE DOKTER");
        tabelmapel.addColumn("NAMA");
        tabelmapel.addColumn("DOKTER BAGIAN");
        tabelmapel.addColumn("JENIS KELAMIN");
        tabelmapel.addColumn("ALAMAT");
        tabelmapel.addColumn("KOTA");
        tabelmapel.addColumn("TELEPON");
        try {
            open_db();
             String sql = "select * from dokter";
            stm = (Statement) Con.createStatement();
            ResultSet rs=stm.executeQuery(sql);
            while (rs.next()) {
                Object[] o =new Object[7];
                o[0] = rs.getString("kd_dokter");
                o[1] = rs.getString("nama_dokter");
                o[2] = rs.getString("bagian");
                o[3] = rs.getString("jenis_kelamin");
                o[4] = rs.getString("alamat");
                o[5] = rs.getString("kota");
                o[6] = rs.getString("telepon");
                tabelmapel.addRow(o);
                
            }
           jTable1.setModel(tabelmapel);
        } catch (Exception e) {
        }
    }

    private void open_db() {
        try {
            Class.forName("com.mysql.jdbc.Driver");
Con = (Connection) DriverManager.getConnection("jdbc:mysql://127.0.0.1/RumahSakit", "root", "");
            stm = (Statement) Con.createStatement();

        }catch (Exception e){
            JOptionPane.showMessageDialog(null,"Koneksi gagal");
            System.out.println(e.getMessage());
        }
    }
private void otomatis(){
        try {
            open_db();
            String sql="select right (kd_dokter,2)+1 from dokter";
            ResultSet rs=stm.executeQuery(sql);
            txtnama.setText("");
            alamat.setText("");
            kota.setText("");
            telepon.setText("");
            if(rs.next()){
                rs.last();
                String no=rs.getString(1);
                while (no.length()<3){
                    no="0"+no;
                    txtid.setText("D"+no);}
                }
            else
            {
                txtid.setText("D001"); 
            }
            } catch (Exception e) 
            {
        }
    }
    private void aktif(boolean x) {
      txtnama.setEditable(x);
      dokter_bagian.setEditable(x);
      jkelamin.setEditable(x);
      alamat.setEditable(x);
      txtid.requestFocus();}
    
      private void siapisi(boolean a) {
       txtid.setEnabled(a);
       txtnama.setEnabled(a);
       dokter_bagian.setEnabled(a);
       jkelamin.setEnabled(a);
       alamat.setEnabled(a);
       kota.setEnabled(a);
       telepon.setEnabled(a);
    }

    private void setTombol(boolean t) {
     bttambah.setEnabled(t);
     btsimpan.setEnabled(!t);
     bthapus.setEnabled(!t);
     btedit.setEnabled(!t);
     btbatal.setEnabled(!t);
    }
