    private void tampilkan_data() {
        DefaultTableModel tabelmapel = new DefaultTableModel();
        tabelmapel.addColumn("KODE RUJUKAN");
        tabelmapel.addColumn("KODE DOKTER");
        tabelmapel.addColumn("NAMA DOKTER");
        tabelmapel.addColumn("KODE PASIEN");
        tabelmapel.addColumn("NAMA PASIEN");
        tabelmapel.addColumn("UMUR PASIEN");
        tabelmapel.addColumn("TANGGAL MASUK");
        tabelmapel.addColumn("RS RUJUKAN");
        try {
            open_db();
            sql = "select * from rujukan";
            stm = (Statement) Con.createStatement();
            ResultSet rs = stm.executeQuery(sql);
            while (rs.next()) {
                Object[] o = new Object[10];
                o[0] = rs.getString("kd_inap");
                o[1] = rs.getString("kd_dokter");
                o[2] = rs.getString("nama_dokter");
                o[3] = rs.getString("kd_pasien");
                o[4] = rs.getString("nama_pasien");
                o[5] = rs.getString("umur");
                o[6] = rs.getString("tgl_periksa");
                o[7] = rs.getString("rumah_sakit");
                tabelmapel.addRow(o);

            }
            jTable1.setModel(tabelmapel);
        } catch (Exception e) {
        }
    }

    private void open_db() {
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Con = (Connection) DriverManager.getConnection("jdbc:mysql://127.0.0.1/RumahSakit", "root", "");
            stm = (Statement) Con.createStatement();

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Koneksi gagal");
            System.out.println(e.getMessage());
        }
    }

    private void otomatis(){
        try {
            open_db();
            String sql="select right (kd_inap,2)+1 from rujukan";
            ResultSet rs=stm.executeQuery(sql);
            kodedokter.setText("");
            namadokter.setText("");
            kodepasien.setText("");
            namapasien.setText("");
            umur.setText("");
            rumahsakit.setText("");
            if(rs.next()){
                rs.last();
                String no=rs.getString(1);
                while (no.length()<3){
                    no="0"+no;
                  kodeinap.setText("RU"+no);}
                }
            else
            {
                kodeinap.setText("RU001"); 
            }
            } catch (Exception e) 
            {
        }
    }
     public static Date getTanggalFromTable(JTable table, int kolom){
        JTable tabel = table;
        String str_tgl = String.valueOf(tabel.getValueAt(tabel.getSelectedRow(), kolom));
        Date tanggal = null;
        try {
            tanggal = new SimpleDateFormat("yyyy-MM-dd").parse(str_tgl);
        } catch (ParseException ex) {
            Logger.getLogger(rujukan.class.getName()).
            log(Level.SEVERE, null, ex);
        }
        return tanggal;
    } 
    private void siapisi(boolean a) {
      kodeinap.setEnabled(a);
      kodedokter.setEnabled(a);
      namadokter.setEnabled(a);
      kodepasien.setEnabled(a);
      namapasien.setEnabled(a);
      umur.setEnabled(a);
      tglmasuk.setEnabled(a);
      rumahsakit.setEnabled(a);
    }
    private void aktif(boolean x) {
        kodepasien.setEditable(x);
        namapasien.setEditable(x);
        kodeinap.requestFocus();
    }

    private void setTombol(boolean t) {
        bttambah.setEnabled(t);
        btsimpan.setEnabled(!t);
        bthapus.setEnabled(!t);
        btedit.setEnabled(!t);
        btbatal.setEnabled(!t);
    }
